import React, { useState } from 'react';
import "./InitialLook.css";
const InitialLook = () => {

  var [authKey, setAuthKey] = useState("");
  function start() {
      fetch("http://localhost:3000/login", {
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json"
      }
    })
    .then(data => data.json()) 
      .then(data => {
        console.log(data);
        window.location.href = data;
      })
  }
  console.log("The URL of this page is: " + window.location.href);
  var url = window.location.href;
  var accessToken = window.location.href.split('code=').pop();  
  setAuthKey(accessToken);
  console.log(accessToken);

  fetch("http://localhost:3000/auth", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
        Accept: "application/json"
    },
    body: JSON.stringify({
      auth: authKey,
      uri: url,
    })
  })
  console.log(accessToken);
  return (
      <div className="InitialLook">
          <div className="text-container">
              <h1 onClick={start}>Start</h1>
          </div>
      </div>
  )
};

export default InitialLook;
